import { useState } from 'react';

export default function UserInput() {
  const [userInput, setUserInput] = useState({
    'current-savings': 10000,
    'yearly-contribution': 1200,
    'expected-return': 7,
    'duration': 10
  });

  function submitHandler(event) {
    event.preventDefault();

    console.log('submit');
  };

  function resetHandler() {
    console.log('reset');
  };

  function changeHandler(input, value) {
    setUserInput((prevInput) => {
      return {
        ...prevInput,
        [input]: value,
      }
    });
  }

  return (
    <form onSubmit={submitHandler } className="form">
        <div className="input-group">
          <p>
            <label htmlFor="current-savings">Current Savings ($)</label>
          <input
            onChange={(event) => changeHandler('current-savings', event.target.value)} type="number" id="current-savings" />
          </p>
          <p>
            <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input  onChange={(event) => changeHandler('yearly-contribution', event.target.value)}
            type="number" id="yearly-contribution" />
          </p>
        </div>
        <div className="input-group">
          <p>
            <label htmlFor="expected-return">
              Expected Interest (%, per year)
            </label>
            <input onChange={(event) => changeHandler('expected-return', event.target.value)} type="number" id="expected-return" />
          </p>
          <p>
            <label htmlFor="duration">Investment Duration (years)</label>
            <input  onChange={(event) => changeHandler('duration', event.target.value)} type="number" id="duration" />
          </p>
        </div>
        <p className="actions">
          <button onClick={resetHandler} type="reset" className="buttonAlt">
            Reset
          </button>
          <button type="submit" className="button">
            Calculate
          </button>
        </p>
      </form>
  )
}
